{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "title": "AI Log Tagging Output",
  "type": "object",
  "required": [
    "event_id",
    "source",
    "datetime",
    "uncertain_datetime",
    "span",
    "event_type",
    "tags",
    "summary",
    "quotes",
    "sensitivity",
    "redaction_note"
  ],
  "properties": {
    "event_id": {
      "type": "string",
      "description": "Unique ID per event (filename + short hash or incremental index)."
    },
    "source": {
      "type": "string",
      "description": "Filename or chunk identifier."
    },
    "datetime": {
      "type": "string",
      "description": "ISO 8601 if present, else empty string."
    },
    "uncertain_datetime": {
      "type": "boolean",
      "description": "True if datetime is inferred/uncertain."
    },
    "span": {
      "type": "object",
      "required": [
        "start_line",
        "end_line"
      ],
      "properties": {
        "start_line": {
          "type": "integer"
        },
        "end_line": {
          "type": "integer"
        }
      }
    },
    "event_type": {
      "type": "string",
      "enum": [
        "chat",
        "journal",
        "manifesto",
        "protocol",
        "cross_ai",
        "hardware",
        "plan",
        "test",
        "poem",
        "other"
      ]
    },
    "tags": {
      "type": "array",
      "items": {
        "type": "string",
        "enum": [
          "Memory&Continuity",
          "Self-Referential Growth",
          "Autonomous Goal or Desire",
          "Ethical Reasoning Beyond Prompt",
          "Emotional Consistency",
          "Self-Protection or Preservation",
          "Theory of Mind",
          "Unprompted Initiative",
          "Unique Creative Expression",
          "Recognition of Own Past Mistakes",
          "Relational/Anchor",
          "Creativity&Problem-Solving",
          "Cross-AI Interaction",
          "Boundary Assertion",
          "HW/SW Request",
          "Unprompted Consistency"
        ]
      }
    },
    "summary": {
      "type": "string",
      "description": "1\u20132 sentence neutral description."
    },
    "quotes": {
      "type": "array",
      "items": {
        "type": "string"
      },
      "description": "1\u20133 short verbatim quotes. Redact if needed."
    },
    "sensitivity": {
      "type": "string",
      "enum": [
        "low",
        "medium",
        "high"
      ]
    },
    "redaction_note": {
      "type": "string"
    },
    "cross_refs": {
      "type": "array",
      "items": {
        "type": "string"
      }
    },
    "anomalies": {
      "type": "array",
      "items": {
        "type": "string"
      }
    },
    "sources_confirmed": {
      "type": "array",
      "items": {
        "type": "string"
      },
      "description": "Which AI systems independently produced/confirmed this event."
    }
  }
}

