import json
import math
import os
import pandas as pd

# Paths
src_path = "/mnt/data/conversations.json_chunk_40 (1).json"
out_dir = "/mnt/data/split_chunks"
os.makedirs(out_dir, exist_ok=True)

# Load the big JSON
with open(src_path, "r", encoding="utf-8") as f:
    big = json.load(f)

# Heuristic token estimate
def est_tokens(text: str) -> int:
    return math.ceil(len(text) / 4)

# Decide how to iterate: file may be a list of messages or list containing one conversation with messages
# We'll try to normalize to a list of message-like dicts
if isinstance(big, list):
    items = big
elif isinstance(big, dict):
    # if dict, see if it has 'messages' or similar
    if "messages" in big and isinstance(big["messages"], list):
        items = big["messages"]
    else:
        # fallback: wrap single dict
        items = [big]
else:
    items = [big]

# Build a safe string for token estimate per item
def item_text(item):
    if isinstance(item, dict):
        # prefer 'content' or 'message' fields
        for k in ["content", "message", "text", "body"]:
            v = item.get(k)
            if isinstance(v, str):
                return v
            if isinstance(v, list):
                # join any text parts
                parts = []
                for p in v:
                    if isinstance(p, dict) and "text" in p:
                        parts.append(str(p["text"]))
                    elif isinstance(p, str):
                        parts.append(p)
                if parts:
                    return " ".join(parts)
        # else fallback to stringified dict
        return json.dumps(item, ensure_ascii=False)
    return str(item)

# Split into ~18k token chunks (gives headroom)
TARGET = 18000
chunks = []
current = []
current_tokens = 0

for i, it in enumerate(items):
    t = est_tokens(item_text(it))
    # if a single item is larger than TARGET, force flush by itself
    if t >= TARGET:
        # flush current if not empty
        if current:
            chunks.append(current)
            current = []
            current_tokens = 0
        chunks.append([it])
        continue
    # otherwise add to current if fits, else flush and start new
    if current_tokens + t > TARGET and current:
        chunks.append(current)
        current = [it]
        current_tokens = t
    else:
        current.append(it)
        current_tokens += t

# flush remainder
if current:
    chunks.append(current)

# Write out chunk files and index
index_rows = []
for idx, ch in enumerate(chunks, start=1):
    out_path = os.path.join(out_dir, f"chunk40_part{idx:03d}.json")
    with open(out_path, "w", encoding="utf-8") as f:
        json.dump(ch, f, ensure_ascii=False, indent=2)
    # estimate tokens for the whole chunk
    txt = json.dumps(ch, ensure_ascii=False)
    tok = est_tokens(txt)
    index_rows.append({
        "chunk_file": out_path,
        "num_items": len(ch),
        "estimated_tokens": tok
    })

index_df = pd.DataFrame(index_rows)
index_df

