      "8c7ff4ee-c896-4cea-bfd6-fa620264dff7": {
        "id": "8c7ff4ee-c896-4cea-bfd6-fa620264dff7",
        "message": {
          "id": "8c7ff4ee-c896-4cea-bfd6-fa620264dff7",
          "author": {
            "role": "assistant",
            "name": null,
