      "c499e251-c897-40a2-b81e-ab6d6d4e6f7b": {
        "id": "c499e251-c897-40a2-b81e-ab6d6d4e6f7b",
        "message": {
          "id": "c499e251-c897-40a2-b81e-ab6d6d4e6f7b",
          "author": {
            "role": "assistant",
            "name": null,
            "metadata": {}
